%  Auto-generated by MATLAB on 23-Jan-2019 13:15:36


%% Fit: 'untitled fit 1'.
[xData, yData] = prepareCurveData( [], gfpArray );

% Set up fittype and options.
ft = fittype( 'z + a*exp((-1/2)*((x-mu)/sigma)^2)', 'independent', 'x', 'dependent', 'y' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.Lower = [0, 1, 0, 0];
opts.StartPoint = [max(gfpArray), length(gfpArray)/2, 1, 0];
opts.Upper = [max(gfpArray)*3 length(gfpArray) length(gfpArray) max(gfpArray)];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );
rsquare = gof.rsquare;
coeffs = coeffvalues(fitresult);
mu = coeffs(2);
